components/common/navigation-list-new:
{{#if children}}
<a class="navPages-action has-subMenu{{#if is_active}} activePage{{/if}}"
   href="{{url}}" style="white-space:nowrap;">
    {{name}}
    <i class="icon navPages-action-moreIcon" aria-hidden="true">
        <svg><use xlink:href="#icon-chevron-down" /></svg>
    </i>
</a>

<div class="navPage-subMenu _nav-open-flag" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">

    {{#if (lowercase (dashcase name)) '===' "bedding" }}    
      {{> components/common/bedding-nav}}
    {{/if}}
    <ul class="navPage-subMenu-list gg">

        {{#inArray (split "bases,toppers" ",") (lowercase (dashcase name))}}    
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a class="navPage-subMenu-action navPages-action has-subMenu{{#if is_active}} activePage{{/if}}"
                       href="{{url}}"
                       aria-label="{{name}}"
                    >
                   
                        <span>{{name}}</span>
                        <span class="collapsible-icon-wrapper"
                            data-collapsible="navPages-{{id}}"
                            data-collapsible-disabled-breakpoint="medium"
                            data-collapsible-disabled-state="open"
                            data-collapsible-enabled-state="closed"
                        >
                            <i class="icon navPages-action-moreIcon" aria-hidden="true">
                                <svg><use xlink:href="#icon-chevron-down" /></svg>
                            </i>
                        </span>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}">
                        {{#each children}}
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action{{#if is_active}} activePage{{/if}}"
                               href="{{url}}"
                               aria-label="{{name}}"
                            >
                                {{name}}
                            </a>
                        </li>
                        {{/each}}
                    </ul>
                {{else}}

                    <a class="navPage-subMenu-action navPages-action{{#if is_active}} activePage{{/if}}"
                       href="{{url}}"
                       aria-label="{{name}}"
                    >

                    <span>{{name}}</span>
 		             
                    <img class="mt-2 mb-2" src="{{cdn (concat 'https://store-z2qdisybty.mybigcommerce.com/content/nav/' (concat  (lowercase (dashcase name)) '.png'))}}"  />
                      <!--BASES -->
                      {{#if (lowercase (dashcase name)) '===' "adjustable-frames"}}
                        <span  class="mt-0 nav-price">Start at <b>$199.00</b></span>
                      {{/if}}
                      {{#if (lowercase (dashcase name)) '===' "platform-beds"}}
                         <span  class="mt-0 nav-price">Start at <b>$130.00</b></span>
                     {{/if}}
                     {{#if (lowercase (dashcase name))  '===' "metal-frames"}}
                          <span  class="mt-0 nav-price">Start at <b>$80.00</b></span>
                     {{/if}}
                     <!--TOPPERS -->
                     {{#if (lowercase (dashcase name))  '===' "2-5-inch-gel-memory-foam-mattress-topper"}}
                         <span  class="mt-0 nav-price">Start at <b>$50.00</b></span>
                    {{/if}}
                    {{#if (lowercase (dashcase name))  '===' "3-inch-down-alternative-mattress-topper"}}
                      <span  class="mt-0 nav-price">Start at <b>$60.00</b></span>
                    {{/if}}
                    {{#if (lowercase (dashcase name))  '===' "carboncool-lt-omniphase-topper"}}
                        <span  class="mt-0 nav-price">Start at <b>$135.00</b></span>
                    {{/if}}
                     <!--BEDDING -->
                    </a>
                {{/if}}
            </li>
        {{/each}}
        {{/inArray}}
    </ul>
</div>
{{else}}
<a class="navPages-action{{#if is_active}} activePage{{/if}}"
   href="{{url}}"
   aria-label="{{name}}"
>

<span>{{name}}</span>
</a>
{{/if}}
